document.addEventListener("DOMContentLoaded",(()=>{document.getElementById("allabc").addEventListener("click",(async()=>{let e=await fetch("users.json"),t=(await e.json()).users.sort((function(e,t){let n=e.lastName.toUpperCase(),l=t.lastName.toUpperCase();return n<l?-1:n>l?1:0})),n=document.getElementById("results");n.textContent="",document.getElementById("restable").innerHTML="";for(let e of t){let t=document.createElement("li");t.innerHTML=e.lastName.toUpperCase()+" "+e.firstName,n.appendChild(t)}})),document.getElementById("connection").addEventListener("click",(async()=>{let e=await fetch("users.json"),t=(await e.json()).users.sort((function(e,t){let n=e.username.toUpperCase(),l=t.username.toUpperCase();return n<l?-1:n>l?1:0}));document.getElementById("results").textContent="";let n=document.getElementById("restable");n.innerHTML="";for(let e of t){let t=document.createElement("tr"),l=document.createElement("td"),a=document.createElement("td"),d=document.createElement("td");l.textContent=e.username,a.textContent=e.email,d.textContent=e.phone,t.appendChild(l),t.appendChild(a),t.appendChild(d),n.appendChild(t)}})),document.getElementById("heightweight").addEventListener("click",(async()=>{let e=await fetch("users.json"),t=(await e.json()).users.filter((e=>e.height>=parseFloat(document.getElementById("tall").value))),n=document.getElementById("results");n.textContent="",document.getElementById("restable").innerHTML="";let l=0;for(let e of t)l+=e.weight;let a=document.createElement("li");a.innerHTML=l+"kg az össz tömegük",n.appendChild(a)})),document.getElementById("brown").addEventListener("click",(async()=>{let e=await fetch("users.json"),t=(await e.json()).users.filter((e=>"Brown"===e.eyeColor)),n=document.getElementById("results");n.textContent="",document.getElementById("restable").innerHTML="";let l=document.createElement("li");l.innerHTML=t.length+" barna szemű van",n.appendChild(l)}))}));